                "subtopics": [
                    "Problem-Solving Agents",
                    "Uninformed Search Strategies (BFS, DFS, UCS, IDS)",
                    "Heuristic Search and Informed Search Strategies (Greedy Best-First, A* Search)",
                    "Local Search Algorithms (Hill Climbing, Simulated Annealing, Genetic Algorithms)",
                    "Adversarial Search (Game Playing, Minimax Algorithm, Alpha-Beta Pruning)",
                    "Constraint Satisfaction Problems (CSP)"
                ]
            },
            {
                "main-topic": "Knowledge Representation and Reasoning",
                "subtopics": [
                    "Logical Agents and Propositional Logic (Syntax, Semantics, Inference)",
                    "First-Order Logic (Syntax, Semantics, Quantifiers, Inference)",
                    "Inference in First-Order Logic (Unification, Resolution)",
                    "Rule-Based Systems and Expert Systems",
                    "Introduction to Ontologies and Semantic Web"
                ]
            },
            {
                "main-topic": "Handling Uncertainty",
                "subtopics": [
                    "Review of Probability Theory",
                    "Bayesian Networks (Representation, Conditional Independence)",
                    "Inference in Bayesian Networks (Exact and Approximate)",
                    "Hidden Markov Models (HMMs)",
                    "Introduction to Decision Theory and Utility Functions"
                ]
            },
            {
                "main-topic": "Foundations of Machine Learning",
                "subtopics": [
                    "Overview of Machine Learning (Supervised, Unsupervised, Reinforcement Learning)",
                    "Training, Validation, and Test Sets",
                    "Bias-Variance Trade-off",
                    "Overfitting and Underfitting",
                    "Model Evaluation Metrics (Accuracy, Precision, Recall, F1-Score, ROC Curves)"
                ]
            },
            {
                "main-topic": "Supervised Learning Algorithms",
                "subtopics": [
                    "Linear Regression and Logistic Regression",
                    "Decision Trees (ID3, C4.5, CART)",
                    "Ensemble Methods (Random Forests, Boosting - AdaBoost, Gradient Boosting)",
                    "Support Vector Machines (SVMs)",
                    "K-Nearest Neighbors (K-NN)"
                ]
            },
            {
                "main-topic": "Neural Networks and Deep Learning",
                "subtopics": [
                    "Perceptrons and Activation Functions",
                    "Feedforward Neural Networks Architecture",
                    "Backpropagation Algorithm",
                    "Deep Neural Networks (DNNs) and Architectures",
                    "Convolutional Neural Networks (CNNs) for Image Processing",
                    "Recurrent Neural Networks (RNNs) for Sequential Data",
                    "Introduction to Transformers and Attention Mechanisms",
                    "Regularization Techniques (Dropout, Batch Normalization)",
                    "Optimization Algorithms (SGD, Adam, RMSProp)"
                ]
            },
            {
                "main-topic": "Unsupervised Learning",
                "subtopics": [
                    "Clustering Algorithms (K-Means, Hierarchical Clustering, DBSCAN)",
                    "Dimensionality Reduction Techniques (Principal Component Analysis - PCA)",
                    "Introduction to Association Rule Learning"
                ]
            },
            {
                "main-topic": "Reinforcement Learning",
                "subtopics": [
                    "Introduction to Reinforcement Learning Concepts (Agent, Environment, State, Action, Reward)",
                    "Markov Decision Processes (MDPs)",
                    "Value Iteration and Policy Iteration",
                    "Q-Learning Algorithm",
                    "SARSA Algorithm",
                    "Introduction to Deep Reinforcement Learning (DQN)"
                ]
            },
            {
                "main-topic": "Natural Language Processing (NLP)",
                "subtopics": [
                    "Introduction to NLP Tasks (Tokenization, POS Tagging, NER)",
                    "Text Representation Techniques (Bag-of-Words, TF-IDF, Word Embeddings)",
                    "Language Models (N-gram Models, Neural Language Models)",
                    "Sequence Models for NLP (RNNs, LSTMs, Transformers in NLP)",
                    "Introduction to Machine Translation"
                ]
            },
            {
                "main-topic": "Computer Vision Fundamentals",
                "subtopics": [
                    "Introduction to Computer Vision Tasks",
                    "Image Representation and Preprocessing",
                    "Feature Extraction Basics",
                    "Image Classification with Deep Learning (CNNs)",
                    "Introduction to Object Detection and Recognition"
                ]
            },
            {
                "main-topic": "Advanced Topics and Societal Impact of AI",
                "subtopics": [
                    "AI Ethics, Bias, and Fairness",
                    "AI Safety and Alignment",
                    "Explainable AI (XAI)",
                    "Current Research Frontiers in AI",
                    "AI in Robotics and Autonomous Systems",
                    "Future Directions and Challenges in AI"
                ]
            }
        ]
    }
}

